    <script type="text/javascript">
    window.onload = function() {
	$('#check-btn').click(function() {
		whoischeckWebsite();
	});
	$('#check-websiteurl').keypress(function(event) {
			if (event.which === 13) {
				whoischeckWebsite();
			}
		});
    }

function whoischeckWebsite() {
	var websiteUrl = $('#check-websiteurl').val();
	$('#check-btn').text("Please wait..");
	if (!$('.wsc_status').hasClass("hidden")) {
		$('.wsc_status').addClass("hidden");
	}
	if (!$('#whois-element').hasClass("hidden")) {
		$('#whois-element').addClass("hidden");
	}
	$("#bar_loading").show().animate({
		width: 20 + 80 * Math.random() + "%"
	}, 200);
	$.ajax({
		url: "https://host.4cards.store/xhr.php?first=whois",
		type: 'GET',
		data: {
			websiteUrl: websiteUrl
		},
		success: function(data) {
		    data = JSON.parse(data);
			if (data.status == 200) {
				$('#whois-element').removeClass("hidden");
				$('#whois-element').html(data.html);
				if (data.createScreenshot == true) {
					const settings = {
						async: true,
						crossDomain: true,
						url: 'https://screenshot-snapshot-site2.p.rapidapi.com/api/v1/screenshot',
						method: 'POST',
						headers: {
							'content-type': 'application/json',
							'Content-Type': 'application/json',
							Accept: 'application/json',
							'X-RapidAPI-Key': '7fdac8677amsh9698b88620066aep11df6fjsnae93c0d69361',
							'X-RapidAPI-Host': 'screenshot-snapshot-site2.p.rapidapi.com'
						},
						processData: false,
						data: '{"url": "http://' + data.url + '","format": "png","width": 1280,"height": 720,"delay": 0,"fullSize": false,"hideCookie": false}'
					};
					$.ajax(settings).done(function(response) {
						$(".whois_site_img").html("<img src='" + response.link + "'>");
					});
				}
			} else {
				$('.wsc_status').removeClass("hidden");
				$('.wsc_status span').html(data.message);
			}
			$('#check-btn').html('<svg width="22" height="22" viewBox="0 0 22 22" fill="none" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" clip-rule="evenodd" d="M2.77734 10.7666C2.77734 6.35468 6.35394 2.77808 10.7659 2.77808C15.1779 2.77808 18.7545 6.35468 18.7545 10.7666C18.7545 15.1786 15.1779 18.7552 10.7659 18.7552C6.35394 18.7552 2.77734 15.1786 2.77734 10.7666ZM10.7659 0.778076C5.24937 0.778076 0.777344 5.25011 0.777344 10.7666C0.777344 16.2832 5.24937 20.7552 10.7659 20.7552C13.045 20.7552 15.1458 19.9919 16.8266 18.707L19.8354 21.708C20.2265 22.098 20.8596 22.0972 21.2496 21.7062C21.6396 21.3151 21.6388 20.682 21.2478 20.292L18.2875 17.3393C19.8236 15.5828 20.7545 13.2834 20.7545 10.7666C20.7545 5.25011 16.2824 0.778076 10.7659 0.778076Z" fill="currentColor"/></svg> Search');
			$("#bar_loading").animate({
				width: "100%"
			}, 200).fadeOut(300, function() {
				$(this).width("0")
			});
		},
		error: function(xhr, status, error) {
			console.error(xhr.responseText);
			$("#bar_loading").animate({
				width: "100%"
			}, 200).fadeOut(300, function() {
				$(this).width("0")
			});
		}
	});
}
</script>
